import { Component, OnInit, ViewChild } from '@angular/core';
import { Code } from '../shared/models/code.interface';
import { Observable } from 'rxjs';
import { CodeService } from '../shared/models/services/code.service';
import { HttpClient } from '@angular/common/http';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MomentModule } from 'ngx-moment';
import {
  FormBuilder,
  FormGroup,
  FormArray,
  FormControl,
  Validators,
} from '@angular/forms';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  name: string;
  codeForm = new FormGroup({
    code: new FormControl(),
  });

  //form: FormGroup;
  errorMsg: string;
  //code = new FormControl('');
  @ViewChild(MatPaginator) paginator: MatPaginator;
  codes = new MatTableDataSource<Code>([]);

  displayedColumns: string[] = ['code'];
  constructor(
    private http: HttpClient,
    private fb: FormBuilder,
    private snackBar: MatSnackBar,
    //public dialog: MatDialogModule
  ) {}

  /*
   * Method to retrieve codes with API call
   */
  fetchCodes(): void {
    this.http
      .get('http://localhost:3000/api/codes')
      .subscribe((res: Code[]) => {
        this.codes.data = res;
        console.log(this.codes.data);
      });
  }

  ngOnInit(): void {
    this.fetchCodes();
    this.codeForm = this.fb.group({
      code: [
        null,
        Validators.compose([
          Validators.required,
          Validators.pattern('^\\d{12}$'), //Allows for only 12 digits
        ]),
      ],
    });
  }

  ngAfterViewInit() {
    this.codes.paginator = this.paginator;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.codes.filter = filterValue.trim().toLowerCase();

    if (this.codes.paginator) {
      this.codes.paginator.firstPage();
    }
  }

  submitCode() {
    const codeInput = this.codeForm.value;

    this.http
      .post('http://localhost:3000/api/codes', {
        // this fixed heroku error, change back to /api/codes for prod
        code: codeInput.code,
      })
      .subscribe((res) => {
        alert('code success');
        window.location.reload(); // Will update to async pipe later
      });
  }

  showSnackbar(content, action, duration) {
    this.snackBar.open(content, action, {
      duration: 1000,
      verticalPosition: 'top', // Allowed values are  'top' | 'bottom'
      horizontalPosition: 'center', // Allowed values are 'start' | 'center' | 'end' | 'left' | 'right'
    });
  }
/*
  openDialog(): void {
    const dialogRef = this.dialog.open(TutDialogComponent, {
      width: '250px',
      data: { name: this.name },
    });

    dialogRef.afterClosed().subscribe((result) => {
      console.log('The dialog was closed');
      this.name = result;
    });
  }*/
}
<meta
  name="description"
  content="Pokemon Go Friend Code finder that lets users submit their own code and add friends by viewing other submitted codes."
/>
<div class="card">
  <button class="navbar-link" routerLink="/" mat-button>Usage Guide</button>
  <div class="center">
    <div><h1>Pokémon Go Friend Codes</h1></div>
    <p class="blurb">
      Welcome, here you can enter you Pokémon Go Friend Code so people can add
      you from all over the world! Simply submit your code and copy codes to add
      in Pokémon Go. Codes can be bumped every 24hrs.
    </p>

    <br />

    <mat-form-field [formGroup]="codeForm">
      <label class="input">
        <input
          matInput
          type="text"
          [formControl]="codeForm.controls['code']"
          class="input__field"
          autofocus
          autocomplete="off"
          id="codeInput"
        />

        <span class="input__label">Your friend code</span>
      </label>
    </mat-form-field>

    <button (click)="submitCode()" [disabled]="codeForm.invalid" id="submitBtn">
      Submit Code
    </button>
    <br />
    <!--<mat-slide-toggle> Toggle QR Codes - Coming soon!</mat-slide-toggle>-->
  </div>
  <br />
  <!-- Code Column -->
  <table mat-table [dataSource]="codes" class="mat-elevation-z8 center2">
    <ng-container matColumnDef="code">
      <th mat-header-cell *matHeaderCellDef class="th">
        Recently submitted friend codes
      </th>
      <td
        mat-cell
        *matCellDef="let code"
        value="code"
        class="code"
        style="cursor: pointer"
        [cdkCopyToClipboard]="code.code"
        (click)="
          showSnackbar('Friend code copied to clipboard', 'Dismiss', '1000')
        "
      >
        {{ code.code }} <a class="date">{{ code.createdAt }}</a>
      </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
  </table>
  <mat-paginator
    [pageSizeOptions]="[10, 20, 50, 100]"
    aria-label="Select page of users"
    style="width: 25vw; margin: 0 auto"
  ></mat-paginator>
</div>
